executionPlan.queryExpressions:
define table abnormalTimes (itemNo string, fromTime long, toTime long) from ('datasource.name'='FraudToolbox', 'database.name'='fraud', 'table.name'='abnormalTimes');

// fraud: abnormal times
from input[((itemNo==abnormalTimes.itemNo and (timestamp>=abnormalTimes.fromTime and timestamp<abnormalTimes.toTime)) in abnormalTimes)]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 1 as fraudflag
insert into fraudStream;

// Not fraud
from input[not((itemNo==abnormalTimes.itemNo and (timestamp>=abnormalTimes.fromTime and timestamp<abnormalTimes.toTime)) in abnormalTimes)]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 0 as fraudflag
insert into outputStream;
