executionPlan.queryExpressions:
define table blacklisted (id int, cardnum long) from ('datasource.name'='FraudToolbox', 'database.name'='fraud', 'table.name'='blacklisted');

from input[((cardnum==blacklisted.cardnum) in blacklisted)]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 1 as fraudflag
insert into fraudStream;

from input[not ((cardnum==blacklisted.cardnum) in blacklisted)]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 0 as fraudflag
insert into outputStream;
