executionPlan.queryExpressions:
from input#window.unique(cardnum)
select txnid, cardnum, count(cardnum) as counter, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp
group by ip
insert into countStream;

from countStream[counter>3]#window.externalTime(timestamp, 60 min) 
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 13.0 as score
insert into scoreStream;

from countStream[counter<=3]#window.externalTime(timestamp, 60 min)
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score
insert into scoreStream;
