executionPlan.queryExpressions:
 define table suspiciousIP (id int, ip string) from ('datasource.name'='FraudToolbox', 'database.name'='fraud', 'table.name'='suspiciousip');
define table suspiciousMail (id int, email string) from ('datasource.name'='FraudToolbox', 'database.name'='fraud', 'table.name'='suspiciousmail');

from input[((ip==suspiciousIP.ip) in suspiciousIP)]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 1 as fraudflag
insert into fraudStream;

from input[((email==suspiciousMail.email) in suspiciousMail)]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 1 as fraudflag
insert into fraudStream;


from input[not((ip==suspiciousIP.ip) in suspiciousIP) and not((email==suspiciousMail.email) in suspiciousMail) ]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 0 as fraudflag
insert into outputStream;
