executionPlan.queryExpressions:
define table FraudCount (cardnum long, txnid string) from ('datasource.name'='FraudToolbox', 'database.name'='fraud', 'table.name'='rejectedtxns');

from input[((cardnum==FraudCount.cardnum) in FraudCount)]
select txnid, input.cardnum, count(input.cardnum) as txnCount, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 1 as fraudflag
group by cardnum
insert into tempStream;

from tempStream[txnCount>3]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 1 as fraudflag
insert into fraudStream;

from tempStream[txnCount<=3]
select txnid, cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 0 as fraudflag
insert into outputStream;

from input[not((cardnum==FraudCount.cardnum) in FraudCount)]
select txnid, input.cardnum, txnamt, currency, email, shippingaddress, billingaddress, ip, itemNo, qty, timestamp, 0.0 as score, 0 as fraudflag
insert into outputStream;
